/** ***************************************************************************************************************************************************************************** *  * @author :fengguangjing * @createTime:2017-6-13下午4:00:13 * @version:4.2.4 * @modifyTime: * @modifyAuthor: * @description: ***************************************************************************************************************************************************************************** */package com.open.react.modules;import java.util.HashMap;import java.util.Map;import android.widget.Toast;import com.facebook.react.bridge.ReactApplicationContext;import com.facebook.react.bridge.ReactContextBaseJavaModule;import com.facebook.react.bridge.ReactMethod;/** *****************************************************************************************************************************************************************************  *  * @author :fengguangjing * @createTime:2017-6-13下午4:00:13 * @version:4.2.4 * @modifyTime: * @modifyAuthor: * @description: *****************************************************************************************************************************************************************************  */public class ToastModule extends ReactContextBaseJavaModule {	private static final String DURATION_SHORT_KEY = "SHORT";	private static final String DURATION_LONG_KEY = "LONG";	public ToastModule(ReactApplicationContext reactContext) {		super(reactContext);	}		@Override	  public String getName() {	    return "ToastAndroid";	  }		 @Override	  public Map<String, Object> getConstants() {	    final Map<String, Object> constants = new HashMap<String, Object>();	    constants.put(DURATION_SHORT_KEY, Toast.LENGTH_SHORT);	    constants.put(DURATION_LONG_KEY, Toast.LENGTH_LONG);	    return constants;	  }	 	 @ReactMethod	  public void show(String message, int duration) {	    Toast.makeText(getReactApplicationContext(), message, duration).show();	  }}